var Koa = require('koa'),
    app = new Koa(),
    path = require('path'),
    jade = require('../index'),
    convert = require('koa-convert');

app.use(jade(path.normalize(__dirname + '/')));

app.use((self, next) => {
    self.state.greet = function() {
        return "Hello!"
    }
    return next().then(() =>{
      self.render('index.jade');
    })
});

app.listen(3000);
